//喂食
function feedPets() {
  return new Promise(resolve => {
    const url = `${weAppUrl}/feed?feedCount=${FEED_NUM}&reqSource=weapp`;
    const host = `draw.jdfcloud.com`;
    const reqSource = 'weapp';
    $.get(taskUrl(url, host, reqSource), async (err, resp, data) => {
      try {
        if (err) {
          console.log('\n京东宠汪汪: API查询请求失败 ‼️‼️')
        } else {
          data = JSON.parse(data);
          if (data.success) {
            if (data.errorCode === 'feed_ok') {
              console.log('喂食成功')
              message += `【喂食成功】${FEED_NUM}g\n`;
            } else if (data.errorCode === 'time_error') {
              console.log('喂食失败：正在食用')
              message += `【喂食失败】您的汪汪正在食用\n`;
            } else if (data.errorCode === 'food_insufficient') {
              console.log(`当前狗粮不够您设定的喂食${FEED_NUM}g, 现为您降低一档次喂食\n`)
              if ((FEED_NUM) === 80) {
                FEED_NUM = 40;
              } else if ((FEED_NUM) === 40) {
                FEED_NUM = 20;
              } else if ((FEED_NUM) === 20) {
                FEED_NUM = 10;
              } else if ((FEED_NUM) === 10) {
                FEED_NUM = 0;
              }
              // 如果喂食设置的数量失败, 就降低一个档次喂食.
              if ((FEED_NUM) !== 0) {
                await feedPets();
              } else {
                console.log('您的狗粮已不足10g')
                message += `【喂食失败】您的狗粮已不足10g\n`;
              }
            } else {
              console.log(`其他状态${data.errorCode}`)
            }
          }
        }
      } catch (e) {
        $.logErr(e, resp);
      } finally {
        resolve();
      }
    })
  })
}
